query.statement:
select sum([Order_OrderNo])
from [OrderView] inner join [CustomerView] on [Order_CustomerID] = [CustomerView].[ID]
where Company_Name = @customerName;

select sum([Order_OrderNo])
from [OrderView] inner join [CustomerView] on [Order_CustomerID] = [CustomerView].[ID]
where [Company_Name] in ({companyNames});
